package com.example.ar_tour;import androidx.lifecycle.MutableLiveData;import androidx.lifecycle.ViewModel;public class SharedviewModel extends ViewModel {    private final MutableLiveData<String> selected = new MutableLiveData<>();    private final MutableLiveData<String> datal =new MutableLiveData<>();    private final MutableLiveData<String> resultsf =new MutableLiveData<>();    private final MutableLiveData<String> resultsh =new MutableLiveData<>();    private final MutableLiveData<String> resultst =new MutableLiveData<>();    public void select(String item){        selected.setValue(item);    }    public String getSelcted(){        return selected.getValue();    }    /*public void displaydata(String data){        datal.setValue(data);    }*/    /*public String getData(){        return datal.getValue();    }*/    public void setResultsF(String result){        resultsf.setValue(result);    }    public String getResultf(){        return resultsf.getValue();    }    public void setResultsh(String resulth){        resultsh.setValue(resulth);    }    public String getResultsh() {        return resultsh.getValue();    }    public void setResultst(String result){        resultst.setValue(result);    }    public String getResultst() {        return resultst.getValue();    }}